/*
	New `creature_template` GUIDs start at 800000
*/

SET @creature_id = 800000;
SET @gossip_menu = 55555;
SET @broadcast_id = 333333;

-- creature_template
DELETE FROM `creature_template` WHERE `entry` = @creature_id;

-- creature_template_addon
DELETE FROM `creature_template_addon` WHERE `entry` = @creature_id;

-- creature_template_wdb
DELETE FROM `creature_template_wdb` WHERE `entry` = @creature_id;

-- gossip_menu
DELETE FROM `gossip_menu` WHERE `entry` > @gossip_menu - 1 AND `entry` < @gossip_menu + 9;

-- gossip_menu_option
DELETE FROM `gossip_menu_option` WHERE `MenuID` > @gossip_menu - 1 AND `MenuID` < @gossip_menu + 9;

-- conditions
DELETE FROM `conditions` WHERE (`SourceTypeOrReferenceId` = 15 OR `SourceTypeOrReferenceId` = 14) AND (`SourceGroup` > @gossip_menu - 1 AND `SourceGroup` < @gossip_menu + 9);

-- smart_scripts
DELETE FROM `smart_scripts` WHERE `entryorguid` = @creature_id;
